export const getAllData = function(data, subject, limit) {
    let limitation = limit
    let i = 0
    const returnData = []
    while (i < limitation) {
        var index = parseInt(Math.random() * data[subject].length, 10)
        returnData.push(data[subject][index])
        data[subject].splice(index, 1)
        i++
    }
    return returnData
}

